  login: async (email, password) => {
    await delay(800);
    // TODO: Replace with actual API call
    if (email === 'admin@stockmaster.com' && password === 'password') {
      return {
        success: true,
        data: {
          token: 'fake-jwt-token-admin',
          user: { id: '1', name: 'Admin User', email, role: 'admin' }
        }
      };
    }
    if (email === 'manager@stockmaster.com' && password === 'password') {
      return {
        success: true,
        data: {
          token: 'fake-jwt-token-manager',
          user: { id: '2', name: 'John Manager', email, role: 'manager' }
        }
      };
    }
    if (email === 'staff@stockmaster.com' && password === 'password') {
      return {
        success: true,
        data: {
          token: 'fake-jwt-token-staff',
          user: { id: '3', name: 'Mike Staff', email, role: 'staff' }
        }
      };
    }
    throw new Error('Invalid credentials');
  },

  register: async (userData) => {
    await delay(800);
    return {
      success: true,
      data: {
        token: 'fake-jwt-token-12345',
        user: { id: Date.now().toString(), ...userData }
      }
    };
  },

  forgotPassword: async (email) => {
    await delay(800);
    return { success: true, message: 'OTP sent to email' };
  },

  verifyOtp: async (email, otp) => {
    await delay(800);
    if (otp === '123456') {
      return { success: true, message: 'OTP verified' };
    }
    throw new Error('Invalid OTP');
  },

  resetPassword: async (email, newPassword) => {
    await delay(800);
    return { success: true, message: 'Password reset successful' };
  },

  getProfile: async () => {
    await delay(500);
    return {
      success: true,
      data: { id: '1', name: 'Admin User', email: 'admin@stockmaster.com', role: 'admin' }
    };
  }
};

