        { id: '4', name: 'Copper Wire', sku: 'ELC-001', category: 'Electrical', stock: 500, minStock: 100, unitOfMeasure: 'm', warehouse: 'Main Warehouse', location: 'Rack A - Shelf 3', price: 12 },
        { id: '5', name: 'LED Bulbs', sku: 'ELC-002', category: 'Electrical', stock: 0, minStock: 50, unitOfMeasure: 'pcs', warehouse: 'Warehouse B', location: 'Rack B - Shelf 1', price: 8 },
        { id: '6', name: 'Aluminum Sheets', sku: 'STL-002', category: 'Raw Materials', stock: 75, minStock: 20, unitOfMeasure: 'pcs', warehouse: 'Main Warehouse', location: 'Floor Storage - Zone A', price: 85 },
