  getById: async (id) => {
    await delay(400);
    return {
      success: true,
      data: { id, name: 'Steel Rods', sku: 'STL-001', category: 'Raw Materials', stock: 150, minStock: 50, unitOfMeasure: 'kg', warehouse: 'Main Warehouse', location: 'Rack A - Shelf 1', price: 45 }
